INSERT INTO `guidelines`(`state`,`data`)VALUES('lms.coursedetails','{"order":4,"position":"bottom","selector":"#cdt_cvns","template":"<div style=\\"width:60rem;margin-bottom:.5rem;\\">Liste l''ensemble des fils de discussions relatifs au cours.<\\/div><div style=\\"margin-bottom:.5rem;\\">L''\\u00e9tudiant peut cr\\u00e9er une nouvelle discussion en cliquant sur <span class=\\"btn green\\"><span class=\\"i i-plus\\"><\\/span><span>New discussion<\\/span><\\/span><\\/div><div style=\\"margin-bottom:.5rem;\\">Il peut \\u00e9galement acc\\u00e9der au fil de discussion en cliquant sur son intitul\\u00e9. Il peut ensuite prendre part \\u00e0 la discussion en cliquant sur <span class=\\"btn green\\">Post a reply<\\/span><\\/div><div>Le bouton <span class=\\"green btn\\"><span class=\\"i i-chevron-left\\"><\\/span><span>Back to discussions<\\/span><\\/span> permet de revenir \\u00e0 l''ensemble des fils de discussions.<\\/div>"}');
